import React, { useEffect, useState } from "react"
import styled from "styled-components"
import imageUrlBuilder from "@sanity/image-url"
import sanityClient from "../Client"

const Gnistan = () => {
  const [client, setClient] = useState("")

  useEffect(() => {
    const aboutQuery = `*[_type == "about"]{
			description, title, image, tagline
		  }`
    sanityClient.fetch(aboutQuery).then((about) => {
      about.forEach((about) => {
        setClient(about)
      })
    })

    return
  }, [])

  return (
    <>
      <Container>
        <LeftContainer>
          <aboutText>{about.title}</aboutText>
          <aboutTagline>{about.tagline}</aboutTagline>
          <Text>{about.description}</Text>
        </LeftContainer>
        <RightContainer></RightContainer>
      </Container>
      <HeroImage
        alt="hero image"
        className="heroimage"
        id="heroimage"
        src={urlFor(about.image).url()}
      />
    </>
  )
}

export default Gnistan

const builder = imageUrlBuilder(sanityClient)
function urlFor(source) {
  return builder.image(source)
}

const Container = styled.div`
  width: 100%;
  height: 100;

  @media screen and (max-width: 450px) {
    height: auto;
    flex-flow: column;
  }
`

const LeftContainer = styled.div`
  color: #fff;
  width: 50%;
  display: flex;
  padding: 10vh 0 0 50px;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  margin: 0;
`

const RightContainer = styled.div`
  height: 100%;
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
  width: 50%;
  padding: 42rem 0 22rem 0;
`

const aboutText = styled.h1`
  color: white;
  font-size: 32px;
  text-align: left;
  cursor: pointer;
`
const aboutTagline = styled.h1`
  color: white;
  font-size: 22px;
  text-align: left;
  cursor: pointer;

  @media screen and (max-width: 1000px) {
    font-size: 22px;
  }
  @media screen and (max-width: 700px) {
  }
  @media screen and (max-width: 500px) {
    font-size: 18px;
  }
  @media screen and (max-width: 400px) {
    font-size: 14px;
  }
`

const HeroImage = styled.img`
  position: absolute;
  height: auto;
  width: 100%;
  right: 0;
  top: 0;
  z-index: -1;
  @media screen and (min-width: 1000px) {
    min-height: 100vh;
  }
`

const Text = styled.p``
