//   height: 100%;
//   right: 0;
//   top: 0;
//   z-index: -1;

//   @media screen and (min-width: 1500px) {
//     height: 100%;
//   }
// `

const HeroImage2 = styled.img`
  width: 500px;
  height: 500px;
`

const builder = imageUrlBuilder(sanityClient)
function urlFor(source) {
  return builder.image(source)
}
