import "./App.css"
import { useState, useEffect} from 'react'
import styled from "styled-components"
import { Route, Switch } from "react-router-dom"
import About from "./pages/about.component"
import Kontakt from "./pages/kontakt.component"
import Home from "./pages/home.component"
import Sidebar from "./components/navbar/sidebar"
import Gnistan from "./pages/Gnistan"
import Osignat from "./pages/Osignat"
import Forsbergs from "./pages/forsbergs"
import sanityClient from '../client'

const Container = styled.div`
  min-height: 80vh;
`



function App() {

  const [vemarjag, setVemarjag] = useState("")
  const [forsbergs, setForsbergs] = useState('')
  const [priskategori, setPriskategori] = useState("")
  const [about, setAbout] = useState("")
  const [client, setClient] = useState("")
  const [header, setHeader] = useState("")

  useEffect(() => {
    const headerQuery = `*[_type == "header"]{
			heroImage, title, tagline, logo
		}`
    sanityClient.fetch(headerQuery).then((header) => {
      header.forEach((header) => {
        setHeader(header)
      })
    })

    return
  }, [])

  useEffect(() => {
    const headerQuery = `*[_type == "client"]{
			  clientName, description, logo, websiteImage, tagline
		  }`
    sanityClient.fetch(headerQuery).then((header) => {
      header.forEach((header) => {
        setClient(header)
      })
    })

    return
  }, [])

  useEffect(() => {
    const aboutQuery = `*[_type == "about"]{
			description, title, image, tagline
		  }`
    sanityClient.fetch(aboutQuery).then((about) => {
      about.forEach((about) => {
        setAbout(about)
      })
    })

    return
  }, [])

  useEffect(() => {
    const priskategoriQuery = `*[_type == "priskategori"]{
			title, icon, price, valuta
		  }`
    sanityClient.fetch(priskategoriQuery).then((priskategori) => {
      priskategori.forEach((priskategori) => {
        setPriskategori(priskategori)
      })
    })

    return
  }, [])

  useEffect(() => {
    const forsbergsQuery = `*[_type == "forsbergs"]{
      title, tagline, description, image
      }`
      if(!forsbergs.length) {
        console.log('fetching')
        sanityClient.fetch(forsbergsQuery).then((forsbergs) => {
          const forsbergsArray = []
          forsbergs.forEach((forsbergs) => {
            forsbergsArray.push(forsbergs)
          })
          setForsbergs(forsbergsArray)
        })

      }


    return
  }, [forsbergs.length])

  useEffect(() => {
    const vemarjagQuery = `*[_type == "vemarjag"]{
			title, tagline, description, image
		  }`
    sanityClient.fetch(vemarjagQuery).then((vemarjag) => {
      const vemarjagArray = []
      vemarjag.forEach((vemarjag) => {
        vemarjagArray.push(vemarjag)
      })
      setVemarjag(vemarjagArray)
    })
    return
  }, [])


  return (
    <Container className="App">
      <Sidebar />
      <Switch>
        <Route exact path="/" header={Home} />
        <Route exact path="/gnistan" about={Gnistan} />
        <Route exact path="/osignat" client={Osignat} />
        <Route exact path={"/about"} vemarjag={About} />
        <Route exact path={"/kontakt"} priskategori={Kontakt} />
        <Route exact path={"/forsbergs"} forsbergs={Forsbergs} />
      </Switch>
    </Container>
  )
}

export default App
