import React from "react"
import styled from "styled-components"
import sanityClient from "../../Client"
import imageUrlBuilder from "@sanity/image-url"

const Header = ( header ) => {

  return (
    <Container>
      <ColumnLeft>
        <HeaderText>{header.title}</HeaderText>
        <HeaderTagline>{header.tagline}</HeaderTagline>
      </ColumnLeft>
      <RightContainer>
        <HeroImage2
          alt="hero image"
          className="heroimage"
          id="heroimage"
          src={urlFor(header.logo).url()}
        />
      </RightContainer>
    </Container>
  )
}

export default Header

const Container = styled.div`
  padding-top: 10px;
  padding-left: 200px;
  height: auto;
  height: 90vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: black;
  gap: 100px;

  @media screen and (max-width: 600px) {
    flex-flow: column;
    padding-left: 100px;
    gap: 10px;
  }
  @media screen and (max-width: 500px) {
    padding-left: 100px;
  }
`

const ColumnLeft = styled.div`
  color: #fff;
  width: 55%;
  display: flex;
  padding: 0;
  flex-direction: column;
  justify-content: left;
  align-items: left;
  margin: 0;

  @media screen and (max-width: 500px) {
    width: 90%;
  }
`
const RightContainer = styled.div`
  height: auto;
  width: 45%;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  padding: 0 5vw 0 0;

  @media screen and (max-width: 420px) {
    width: 100%;
    padding: 0;
  }
`

const HeaderText = styled.h1`
  color: white;
  font-size: 4rem;
  min-width: 120px;
  text-align: left;
  justify-self: left;

  @media screen and (max-width: 500px) {
    min-width: 235px;
    font-size: 2rem;
  }
`

const HeaderTagline = styled.p`
  color: white;
  font-size: 1.7rem;
  text-align: left;
  line-height: 1.1;

  @media screen and (max-width: 1000px) {
    font-size: 22px;
  }
  @media screen and (max-width: 700px) {
  }
  @media screen and (max-width: 500px) {
    font-size: 18px;
    width: 301px;
  }
  @media screen and (max-width: 420px) {
    font-size: 17px;
    min-width: 150px;
    max-widtH: 250px;
  }
`

const HeroImage2 = styled.img`
  width: 550px;
  height: 550px;

  @media screen and (max-width: 1000px) {
    width: 370px;
    height: auto;
  }
  @media screen and (max-width: 700px) {
    width: 301px;
    height: auto;
  }
  @media screen and (max-width: 500px) {
    width: 250px;
    height: auto;
  }
  @media screen and (max-width: 420px) {
    width: 270px;
  }
`

const builder = imageUrlBuilder(sanityClient)
function urlFor(source) {
  return builder.image(source)
}
